mapper.select:
select
from t_class where is_delete = 0
limit #{length} offset #{start}

select 
t_class.class_id,t_class.class_name,t_class.student_count,t_class.responsible,t_class.telephone,t_class_series.class_series_name 
from (t_class LEFT JOIN t_class_class_series ON t_class.class_id = t_class_class_series.class_id) LEFT JOIN t_class_series on t_class_class_series.class_series_id = t_class_series.class_series_id;
where t_class.is_delete = 0

	limit #{length} offset #{start}

mapper.insert:
insert into t_class (class_id, class_name, student_count, 
  state, responsible, telephone, 
  is_delete, create_by, create_at
  )
values (#{classId,jdbcType=INTEGER}, #{className,jdbcType=VARCHAR}, #{studentCount,jdbcType=INTEGER}, 
  #{state,jdbcType=INTEGER}, #{responsible,jdbcType=VARCHAR}, #{telephone,jdbcType=VARCHAR}, 
  #{isDelete,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}, #{createAt,jdbcType=TIMESTAMP}
  )
