  #{startTime,jdbcType=DATE}, #{endTime,jdbcType=DATE}, #{state,jdbcType=INTEGER}, 
  #{isDelete,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}, #{createAt,jdbcType=TIMESTAMP}
  )
