mapper.select:
select 

from t_student_info
where student_id = #{studentId,jdbcType=INTEGER}
  and card_id = #{cardId,jdbcType=INTEGER}
  and fingerprint_id = #{fingerprintId,jdbcType=INTEGER}
  and avatar_id = #{avatarId,jdbcType=INTEGER}

mapper.delete:
